findings.code_snippet:
resource "aws_dynamodb_table" "document_registry" {
  name           = "${var.project_name}-document-registry"
  billing_mode   = "PAY_PER_REQUEST"
  hash_key       = "document_id"
  range_key      = "version"

  attribute {
    name = "document_id"
    type = "S"
  }

  attribute {
    name = "version"
    type = "N"
  }

  attribute {
    name = "status"
    type = "S"
  }

  attribute {
    name = "upload_timestamp"
    type = "S"
  }

  global_secondary_index {
    name            = "status-upload_timestamp-index"
    hash_key        = "status"
    range_key       = "upload_timestamp"
    projection_type = "ALL"
  }

  # Customer-managed KMS encryption (when enabled)
  dynamic "server_side_encryption" {
    for_each = var.use_customer_managed_encryption ? [1] : []
    content {
      enabled     = true
      kms_key_arn = aws_kms_key.storage[0].arn
    }
  }

  tags = merge(var.tags, {
    Name        = "${var.project_name}-document-registry"
    Purpose     = "Document metadata tracking"
    Environment = var.environment
  })
}

